import React, { useEffect, useState } from "react";
import { useFormContext } from "react-hook-form";
import SelectSearch from "react-select-search";

export const FormOne = ({ formContent }) => {
  const methods = useFormContext();
  const { reset, register } = methods;
  const options = [
    {name: 'User1', value: '1'},
    {name: 'User2', value: '2'}
];

  useEffect(() => {
    reset({ ...formContent.one }, { errors: true });
  }, []);

  return (
    <form>
      <SelectSearch
        name="selectUser"
        options={options}
        {...register('selectUser', { required: true })}
      />
      <input
        name="amount"
        placeHolder="amount to transfer"
        {...register('amount', { required: true })}
      />
    </form>
  );
};

export const FormTwo = ({ formContent }) => {
  const methods = useFormContext();
  const { reset, register } = methods;

  useEffect(() => {
    reset({ ...formContent.two }, { errors: true });
  }, []);

  return (
    <form>
      <input
        name="example"
        defaultValue="test"
        {...register('test', { required: true })}
      />

    </form>
  );
};

export const FormThree = ({ formContent }) => {
  const methods = useFormContext();
  const { reset, register } = methods;

  useEffect(() => {
    reset({ ...formContent.three }, { errors: true });
  }, []);

  return (
    <form>
      <input
        name="example"
        defaultValue="test"
        {...register('test', { required: true })}
      />
    </form>
  );
};
