import React, { useState } from "react";
import ChatBot from "react-simple-chatbot";
import { ThemeProvider } from "styled-components";
//import botAvatar from "https://cdn-icons-png.flaticon.com/512/1847/1847657.png";
//import userAvatar from "https://thumbs.dreamstime.com/z/user-icon-trendy-flat-style-isolated-grey-background-user-symbol-user-icon-trendy-flat-style-isolated-grey-background-123663211.jpg";

const theme = {
  background: "#f5f8fb",
  fontFamily: "Helvetica",
  headerBgColor: "#00796B",
  headerFontColor: "#fff",
  headerFontSize: "15px",
  botBubbleColor: "#e1ffc7",
  botFontColor: "#000",
  userBubbleColor: "#fff",
  userFontColor: "#000"
};

const balance= 20;

function Chat() {
  const [firstName, setFirstName] = useState("");

  return (
      <ThemeProvider theme={theme}>
        <ChatBot
          width="450px"
          //botAvatar={botAvatar}
          //userAvatar={userAvatar}
          botDelay={200}
          userDelay={0}
          steps={[
            {
              id: "10",
              message:"What You need help?",
              trigger: "20"
            },
            {
                id:"20",
                options: [
                    { value: 1, label: "Loans", trigger: "3" },
                    { value: 2, label: "Your details", trigger: "4" },
                    { value: 3, label: "Another", trigger: "5" },
                ],
            },
            {
                id:"3",
                message:"Go to My Loans->",
                end: true,
            },
            {
                id:"4",
                options: [
                    { value: 1, label: "My balance in LevCoins", trigger: "6" },
                    { value: 2, label: "My balance in dollars", trigger: "7" },
                ],
            },
            {
                id:"5",
                message:"write your request",
                trigger:8,
            },
            {
                id:"6",
                message:`Your Balance is ${balance} LevCoins`,
                end: true,
            },
            {
                id:"7",
                message:`Your Balance is ${balance} $`,
                end: true,
            },
            {
                id:"8",
                user:true,
                trigger:"9",
            },
            {
                id:"9",
                message:"Go to Contact Us ->",
                end: true,
            },


          ]}
        />
      </ThemeProvider>
  );
}
export default Chat;
