import React from 'react';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useFormContext } from "react-hook-form";
import _ from "lodash";
import RegisterForm from './DetailsTransfer';
import { FormThree } from './Form_3_ok';

function getSteps() {
  return ["One", "Two", "Three"];
}


export default function HorizontalLabelPositionBelowStepper() {
  
  function getStepContent(step, formContent) {
    switch (step) {
      case 0:
        return <FormThree {...{ formContent }} />;
      case 1:
        return <FormTwo {...{ formContent }} />;
      case 2:
        return <FormThree {...{ formContent }} />;
      default:
        return "Unknown step";
    }
  }
  
    const { watch, errors } = useFormContext();
    const [activeStep, setActiveStep] = React.useState(0);
    const [compiledForm, setCompiledForm] = React.useState({});
    const steps = getSteps();
    const form = watch();
  
    const handleNext = () => {
      let canContinue = true;
  
      switch (activeStep) {
        case 0:
          setCompiledForm({ ...compiledForm, one: form });
          canContinue = true;
          break;
        case 1:
          setCompiledForm({ ...compiledForm, two: form });
          canContinue = true;
          break;
        case 2:
          setCompiledForm({ ...compiledForm, three: form });
          canContinue = handleSubmit({ ...compiledForm, three: form });
          break;
        default:
          return "not a valid step";
      }
      if (canContinue) {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
      }
    };
  
    const handleBack = () => {
      if (activeStep > 0) {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
        switch (activeStep) {
          case 1:
            setCompiledForm({ ...compiledForm, two: form });
            break;
          case 2:
            setCompiledForm({ ...compiledForm, three: form });
            break;
          default:
            return "not a valid step";
        }
      }
    };
  
    const handleReset = () => {
      setActiveStep(0);
      setCompiledForm({});
    };
  
    const handleSubmit = form => {
      if (_.isEmpty(errors)) {
        console.log("submit", form);
      }
    };


  return (
    <div className="root">
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className="instructions">All steps completed</Typography>
            <Button onClick={handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            <Typography className="instructions">{getStepContent(activeStep, compiledForm)}
            </Typography>
            <div>
              <Button
                disabled={activeStep === 0}
                onClick={handleBack}
                className="backButton"
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
