import React, { useEffect, useState } from "react";

import { useFormContext } from "react-hook-form";

export const FormOne = ({ formContent }) => {
  const [recevierName, setReceiverName]= useState("");
  const methods = useFormContext();
  const { reset, register } = methods;

  useEffect(() => {
    reset({ ...formContent.one }, { errors: true });
  }, []);

  const handleChangeReceiverName=(name)=>
  {
    setReceiverName(name);
  }

  return (
    <form>
      <TextField
        label="Enter Full Name"
        style={{ margin: 8, width: "93%" }}
        fullWidth
        margin="normal"
        required
        onChange={()=>{handleChangeReceiverName(value)}}
        value={recevierName}
      />
      
    </form>
  );
};

export const FormTwo = ({ formContent }) => {
  const methods = useFormContext();
  const { reset, register } = methods;

  useEffect(() => {
    reset({ ...formContent.two }, { errors: true });
  }, []);

  return (
    <form>
      <input
        name="example"
        defaultValue="test"
        {...register('test', { required: true })}
      />
    </form>
  );
};

export const FormThree = ({ formContent }) => {
  const methods = useFormContext();
  const { reset, register } = methods;

  useEffect(() => {
    reset({ ...formContent.three }, { errors: true });
  }, []);

  return (
    <form>
      <input
        name="example"
        defaultValue="test"
        {...register('test', { required: true })}
      />
    </form>
  );
};
