import React, { useState } from "react";
import ChatBot from "react-simple-chatbot";
import { ThemeProvider } from "styled-components";
//import botAvatar from "https://cdn-icons-png.flaticon.com/512/1847/1847657.png";
//import userAvatar from "https://thumbs.dreamstime.com/z/user-icon-trendy-flat-style-isolated-grey-background-user-symbol-user-icon-trendy-flat-style-isolated-grey-background-123663211.jpg";

const theme = {
  background: "#f5f8fb",
  fontFamily: "Helvetica",
  headerBgColor: "#00796B",
  headerFontColor: "#fff",
  headerFontSize: "15px",
  botBubbleColor: "#e1ffc7",
  botFontColor: "#000",
  userBubbleColor: "#fff",
  userFontColor: "#000"
};
const ChatContext = React.createContext("");

function FirstNameSaver(props) {
  return (
    <ChatContext.Consumer>
      {({ firstName, setFirstName }) => {
        setFirstName(props.previousStep.value);
        return `Hallo, ${firstName}! Wie lautet dein Nachname?`;
      }}
    </ChatContext.Consumer>
  );
}

const BornAt = (props) => {
  return (
    <ChatContext.Consumer>
      {({ firstName, setFirstName }) =>
        `${firstName}, wann bist Du geboren (z.B. 01.04.1971)?`
      }
    </ChatContext.Consumer>
  );
};

function Chat() {
  const [firstName, setFirstName] = useState("");

  return (
    <ChatContext.Provider value={{ firstName, setFirstName }}>
      <ThemeProvider theme={theme}>
        <ChatBot
          width="450px"
          //botAvatar={botAvatar}
          //userAvatar={userAvatar}
          botDelay={200}
          userDelay={0}
          steps={[
            {
              id: "1",
              message:
                "What You need help?",
              trigger: "2"
            },
            {
                id:"2",
                options: [
                    { value: 1, label: "Loans", trigger: "1" },
                    { value: 2, label: "World State", trigger: "2" },
                    { value: 3, label: "update your details", trigger: "3" }
                ],
            },
            {
                id:"1",
                message:"Loans",
            },
            {
                id:"2",
                message:"World state",
            },
            {
                id:"3",
                message:"...",
            }


          ]}
        />
      </ThemeProvider>
    </ChatContext.Provider>
  );
}
export default Chat;
